INSERT INTO [dbo].[Actors] ([Id], [FirstName], [MiddleName], [LastName], [Bio], [DOB], [SexId]) VALUES (3, N'Chadwick', NULL, N'Boseman', N'Chadwick Boseman is an American actor. He is known for his portrayal of T''Challa / Black Panther in the Marvel Cinematic Universe (since 2016), particularly in Black Panther (2018), and for his starring roles in as Jackie Robinson in 42 (2013), James Brown in Get on Up (2014), and Thurgood Marshall in Marshall (2017).', N'1977-11-29 00:00:00', (SELECT TOP(1) [Id] FROM [MovieDatabase].[dbo].[Gender] where Symbol = 'M'))
